export default {
  name: 'PageTransition',
  methods: {
    beforeEnter(el) {
      el.style.opacity = 0;
      el.style.transform = 'translateY(10px)';
    },
    enter(el, done) {
      setTimeout(() => {
        el.style.transition = 'all 0.3s ease-out';
        el.style.opacity = 1;
        el.style.transform = 'translateY(0)';
        done();
      }, 50);
    },
    afterEnter(el) {
      el.style.transition = '';
    },
    enterCancelled(el) {
      // Cleanup if needed
    },
    beforeLeave(el) {
      el.style.opacity = 1;
    },
    leave(el, done) {
      el.style.transition = 'all 0.2s ease-in';
      el.style.opacity = 0;
      el.style.transform = 'translateY(-10px)';
      setTimeout(done, 200);
    },
    afterLeave(el) {
      el.style.transition = '';
      el.style.transform = '';
    },
    leaveCancelled(el) {
      // Cleanup if needed
    }
  }
};
